import React, { Component } from 'react';
import styled from 'styled-components';
import icn from "../icn.svg"



const Searchbar = () => {

        return (

            <Srch>
             {/*https://www.iconfinder.com/icons/1243689/call_phone_icon
                 Creative Commons (Attribution 3.0 Unported);
                https://www.iconfinder.com/Makoto_msk*/}
                <div className='dv bg-blue p-2'>
                <div className='h3 bg-green p-3 m-3'>
                    <input  className="inpt" type="search" placeholder="Search..." />
                    <span className='fas'><img src={icn}/></span>

                </div>    
                </div>
            </Srch>
        );

}

export default Searchbar;

const Srch = styled.div `
     display:flex;
     justify-content:center;

.dv {
    width: auto;
}   

.inpt {
    outline: 0;
    border: 0;
    background: none;
    width: 0;
    caret-color:transparent;
    transition: width 0.5s linear;
    }
.h3:hover .inpt {
    padding: 0 10px;
    width: 18.75em;
    transition: width 0.5s linear;
    background: black;
    caret-color: black;
    text-transform: capitalize;
    font-size: 1.2em;
    font-weight: bold;
    cursor:pointer;
}
span {
    width: 3.5em;
    height: 3.5em;
    justify-content:right;
    display:flex;
    align-items:center;
    margin: 0;
    border-radius: 2em; 

}
`;
