import React, { Component } from 'react';
import styled from 'styled-components';
import icn from "../icn.svg"



const Searchbar = () => {

        return (

            <Srch>
             {/*https://www.iconfinder.com/icons/1243689/call_phone_icon
                 Creative Commons (Attribution 3.0 Unported);
                https://www.iconfinder.com/Makoto_msk*/}
                <div className='dv  p-4'>
                    <div className='h3  p-3 m-4'>

                    <input  className="inpt" type="search"  />
                    <span className='fas'><img src={icn}/></span>

                    </div>    
                </div>
            </Srch>
        );

}

export default Searchbar;

const Srch = styled.div `
     display:flex;
     justify-content:center;

.dv {
    width: auto;
}   

.inpt {
    outline: 0;
    border: 0;
    background: none;
    width: 0;
    caret-color:transparent;
    transition: width 0.3s linear;

    }
.h3:hover .inpt {
    padding: 0 10px;
    width: 8.75em;
    height:2.5em;
    transition: width 0.3s linear;
    background: gray;
    caret-color: black;
    text-transform: capitalize;
    font-size: 1em;
    cursor:pointer;
}
.fas {
    float:right;
    width: 3em;
    height:2.5em;
    padding-bottom: 1em;
    justify-content:center;

}
`;
