                  <div className='db'>

                        <input  className="inpt" type="search" placeholder="Search..." />


                  </div>
               </div>

            </Srch>
        );

}

export default Searchbar;

const Srch = styled.div `
  display:flex;
     justify-content:center;



    .dv {
